INSERT INTO `products` (`pid`, `name`, `category`, `subcategory`, `classified`, `panel`, `description`, `image`, `thumb`, `date`) VALUES
('100372', 'INTRAORAL CAMERA', 'Dental', 'Oral Medicine and Radiology', 'Scanners-and-Camera', 1.3, '<p>\r\nIntraoral Camera Use Japanese 1/4" Sony Super HAD color CCD and Japanese original lens,than CMOS more resolution and sensitivity, High performance\r\nneed to be connected with computer,Works with Windows XP/Vista/7/8!\r\nCompact Size, Easy to use and focus,the high quality materials make the images truely and excellently,the quality of products are stably as well.\r\nHigh quality optically coated auto focus lens,Dentists and patients can observe the states of dental and mouth at the first time\r\nFull digital function, can be used momentarily,Reliable light source with 6 pieces of white light LED\r\n</p>', 'images/products/Dental/Oral-Medicine-and-Radiology/100372.png', '', '2014-07-18 06:37:24'),
('100738', 'MAGNIFYING GLASS WITH LED LIGHT', 'Dental', 'Pedodontics', 'Loupes', 5.2, '<p>\r\n<b>Features:  </b>\r\n</p>                                                                                 \r\n<ol>\r\n<li>Easy operation and comfortable lightweight. Perfect for lengthy wear.\r\n<li>Band adjusts to fit all head sizes.\r\n<li>Can be worn over eyeglasses.\r\n<li>Wide, clear lens plate permit a great amount of light.\r\n<li>Powerful LED Light with Long Life\r\n<li>Battery operated.\r\n</ol>\r\n\r\n\r\n<p>\r\n<b>Application: </b>\r\n</p>\r\n<p>\r\nClinical Use for RCT Treatment and other dental procedures\r\nA substitute for Loupes\r\n</p>\r\n\r\n<p>\r\n<b>Kit Includes</b>\r\n</p>\r\n<ol>\r\n<li>Lens 1.2x \r\n<li>Lens 1.8x \r\n<li>Lens 2.5x \r\n<li>Lens 3.5x\r\n</ol>\r\n', 'images/products/Dental/Pedodontics/100738.png', '', '2014-07-18 08:14:15'),
